OT2.Home = {
    initPage: function() {
        this.lunbo(), this.ppt()
    },
    lunbo: function() {
        $("#J_LunboList").slide({
            mainCell: ".J_LunboItems",
            effect: "leftLoop",
            vis: "XL" == window.SIZE ? 6 : 5,
            interTime: "3000",
            autoPlay: !0
        })
    },
    ppt: function() {
        jQuery(".banner").slide({
            mainCell: ".silderbox > ul",
            effect: "fold",
            autoPlay: !0,
            interTime: "8000",
            delayTime: "1000",
            titCell: ".control-s li"
        })
    }
}, OT2.Home.initPage();