        var refreshCaptcha = function () {
            var htm = $.trim($('#user-captcha-template').html());
            $.get('/site/captcha', { 'refresh': 1 }, function (data) {
                htm = htm.replace('{{url}}', data.url);
                $('.user-captcha').html(htm);
            });
        };
